import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class SizeSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sizes: []
    };
  }
  componentDidUpdate() {
    const sizes = this.props.size;
    if (this.state.sizes.length < 0) {
      this.setState({ sizes: sizes });
    }
    console.log("didUp", sizes);
  }
  render() {
    if (this.props.size) {
      this.state.sizes = this.props.size;
    }

    const dropdown = this.state.sizes.map(size => (
      <Dropdown.Item
        key={size}
        onClick={() => {
          this.props.handleSizeSelection(size);
        }}
      >
        {size}
      </Dropdown.Item>
    ));
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="secondary">
            Size:
            {/* {this.props.selectedSize.length > 0
              ? this.props.selectedSize
              : "Click to choose"} */}
          </Dropdown.Toggle>
          <Dropdown.Menu>{dropdown}</Dropdown.Menu>
        </Dropdown>
      </div>
    );
  }
}
