import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class SizeSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sizes: []
    };
  }
  componentDidMount() {
    // this.setState({ size: this.props.size });
  }
  render() {
    console.log("props in size", this.props);
    const {
      handleSizeSelection,
      selectedSize,
      validateSizeSelection
    } = this.props;
    if (this.props) {
      this.state.sizes = this.props.size;
      console.log("sizes are", this.state.sizes);
    }
    const dropdown = this.state.sizes.map(size => (
      <Dropdown.Item
        key={size}
        onClick={() => {
          return handleSizeSelection(size), validateSizeSelection("valid");
        }}
      >
        {size}
      </Dropdown.Item>
    ));
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle caret outline color="secondary">
            Size: {selectedSize.length > 0 ? selectedSize : "Click to choose"}
          </Dropdown.Toggle>
          <Dropdown.Menu>{dropdown}</Dropdown.Menu>
        </Dropdown>
      </div>
    );
  }
}
