import React, { Component } from "react";
import { Button, Modal, ModalBody, ModalFooter } from "react-bootstrap";
import { FiXSquare } from "react-icons/fi";
import axios from "axios";

class DeleteProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modalEdit: false
    };
  }

  handleDelete = id => {
    axios
      .delete("/products/delete/:id", {
        params: {
          id
        }
      })
      .then(response => {
        console.log(response);
        this.setState({ modalEdit: !this.state.modalEdit });
      })
      .then(() => {
        window.location.reload(true);
      })
      .catch(error => {
        console.log(error);
      });
  };

  toggle = () => this.setState({ modalEdit: !this.state.modalEdit });

  render() {
    const { title, id } = this.props;
    return (
      <div>
        <Button color="danger" size="sm" onClick={this.toggle}>
          <FiXSquare />
        </Button>
        <Modal
          isOpen={this.state.modalEdit}
          toggle={this.toggle}
          className={this.props.className}
        >
          <Modal.Header toggle={this.toggle}>{title}</Modal.Header>
          <Modal.Body>You confirm deleting item ID: {id}?</Modal.Body>
          <ModalFooter>
            <Button color="danger" onClick={() => this.handleDelete(id, title)}>
              Confirm
            </Button>{" "}
            <Button color="secondary" onClick={this.toggle}>
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default DeleteProduct;
