import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class ColorSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      color: []
    };
  }
  setColor = () => {
    this.setState({ color: this.props.color });
    console.log("Color is", this.state.color);
  }
  render() {
    console.log("Props are", this.props.color);
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="info" id="dropdown-basic" onClick={this.setColor}>
            Select Color
          </Dropdown.Toggle>
          {/* <h1>{[sizes]}</h1> */}
          {/* {sizes.map((size, id) => (
              <Dropdown.Menu key={id}>
                <Dropdown.Item>{size}</Dropdown.Item>
                <Dropdown.Item>Another action</Dropdown.Item>
                <Dropdown.Item>Something else</Dropdown.Item>
              </Dropdown.Menu>
            ))} */}
        </Dropdown>
      </div>
    );
  }
}
