import React, { Component } from "react";
import axios from "axios";
import { Card } from "react-bootstrap";

class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: {},
      error: ""
    };
  }
  componentDidMount() {
    const tag = this.props.match.params.tag;
    console.log("Category is params is ", tag);
    axios
      .get(`http://localhost:4000/category/${tag}`)
      .then(data => {
        if (data.data.message) {
          this.setState({ error: data.data.message });
          console.log("error is", this.state.error);
        } else {
          this.setState({ products: data.data });
          console.log("data in  category", this.state.products);
        }
      })
      .catch(err => {
        console.log("error is", err);
      });
  }

  render() {
    return (
      <div>
        <div className="mt-5">
          {" "}
          {this.state.error && <h1>{this.state.error}</h1>}
        </div>
        <div className="mt-5">
          <CardColumns>
            {this.state.products.map((product, id) => (
              <Card key={id}>
                  <Card.Img variant="top" src={movie.imageURL} alt="image" />
                  <Card.Body>
                    <Card.Title>{movie.title}</Card.Title>
                    <Card.Subtitle>{movie.subTitle}</Card.Subtitle>
                    <Card.Text>{movie.description}</Card.Text>
                  </Card.Body>
                  <Card.Body>
                    <Badge variant="primary">{movie.rating}</Badge>
                  </Card.Body>
              </Card>
            ))}
          </CardColumns>
        </div>
          <Button>Add to Cart</Button>
      </div>
    );
  }
}
export default ProductList;
