import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class SizeSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sizes: [],
      selectedSize: ""
    };
  }
  componentDidUpdate() {
    if (this.state.sizes.length === 0) {
      this.setState({ sizes: this.props.size });
    }
    // if (this.state.selectedSize.length === 0) {
    //   this.setState({ selectedSize: this.props.selectedSize });
  }
  render() {
    const dropdown = this.state.sizes.map(size => (
      <Dropdown.Item
        key={size}
        onClick={() => {
          this.props.handleSizeSelection(size);
        }}
      >
        {size}
      </Dropdown.Item>
    ));
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="secondary">
            Size:
            {/* {this.state.selectedSize.length > 0
              ? this.props.selectedSize
              : "Click to choose"} */}
          </Dropdown.Toggle>
          <Dropdown.Menu>{dropdown}</Dropdown.Menu>
        </Dropdown>
      </div>
    );
  }
}
