import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class SizeSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sizes: []
    };
  }
  componentDidMount() {
    // this.setState({ size: this.props.size });
  }
  render() {
    if (this.props.size) {
      this.state.sizes = this.props.size;
      console.log("sizes are", this.state.sizes);
    }
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="info" id="dropdown-basic">
            Select Size
          </Dropdown.Toggle>
          <h1>{[this.state.sizes]}</h1>
          {this.state.sizes.map(size => (
            <Dropdown.Menu key={size}>{size}</Dropdown.Menu>
          ))}
        </Dropdown>
      </div>
    );
  }
}
