import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class ColorSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      color: []
    };
  }
  setColor = () => {
    this.setState({ color: this.props.color });
    console.log("Color is", this.state.color);
  };
  render() {
    console.log("Props are", this.props.color);
    console.log(typeof this.props.color);
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle
            variant="info"
            id="dropdown-basic"
            onClick={this.setColor}
          >
            Select Color
          </Dropdown.Toggle>
          {/* <h1>{[this.state.color]}</h1> */}
          {this.state.color.map((color,id) => (
            <Dropdown.Menu>{color}</Dropdown.Menu>
          ))}
        </Dropdown>
      </div>
    );
  }
}
