import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";
import PropTypes from "prop-types";
const propTpyes = {
  size: PropTypes.array.isRequired
};

export default class ColorSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      colors: []
    };
  }
  render() {
    console.log("colors are", this.props.color);
    if (this.props.color) {
      this.state.colors = this.props.color;
      console.log("in state color is", this.state.colors);
       const dropdown = this.state.colors.map(color => (
        <Dropdown.Item key={color}>{color}</Dropdown.Item>
      ));
    }
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="info" id="dropdown-basic">
            Select Color
          </Dropdown.Toggle>
          {/* <Dropdown.Menu>{dropdown}</Dropdown.Menu> */}
        </Dropdown>
      </div>
    );
  }
}
ColorSelection.propTpyes = propTpyes;
