import React, { Component } from "react";

export default class CustomerInfo extends Component {
  render() {
    const {
      firstName,
      lastName,
      country,
      city,
      province,
      postalCode,
      phoneNumber,
      address1,
      address2,
      shippingMethod,
      formIsValid
    } = this.props;
    return;
    <div style={{ borderBottom: "1px solid grey" }}>
      <ListGroupItem>
        <h3 style={{ cursor: "pointer" }}>
          <Badge color="secondary" pill size="sm">
            1
          </Badge>{" "}
          Customer Info {}
        </h3>
        <Collapse>
          <Row>
            <Col md={6} style={{ paddingTop: "10px" }}>
              <Label for="First name">First name</Label>
              <Input
                type="text"
                onChange={this.props.onChangeFirstName()}
                value={firstName}
              />
            </Col>
            <Col md={6} style={styles.formFieldsSpace}>
              <Label for="exampleEmail">last Name</Label>
              <Input type="text" onChange={this.props.onChangeLastName()} value={lastName} />
            </Col>
            <Col md={12} style={{paddingTop: '10px'}}>
              <Label for="exampleSelect">Country</Label>
              <Input
                type="select"
                name="select"
                onChange={this.props.onChangeCountry()}
                value={country}
              >
                <option value="Pakistan">Pakistan</option>
              </Input>
            </Col>
            <Col md={6} style={{paddingTop: '10px'}}>
              <Label>City</Label>
              <Input type="text" onChange={this.props.onChangeCity()} value={city} />
            </Col>
            <Col md={6} style={{paddingTop: '10px'}}>
              <Label>State/Province</Label>
              <Input type="text" onChange={this.props.onChangeProvince()} value={province} />
            </Col>
            <Col md={6} style={styles.formFieldsSpace}>
              <Label>Postal Code</Label>
              <Input
                type="number"
                onChange={onChangePostalCode}
                value={postalCode}
              />
            </Col>
            <Col md={6} style={styles.formFieldsSpace}>
              <Label>Phone Number</Label>
              <Input
                type="number"
                onChange={onChangePhoneNumber}
                value={phoneNumber}
              />
            </Col>
            <Col md={12} style={styles.formFieldsSpace}>
              <Label for="Address1">Address1</Label>
              <Input type="text" onChange={onChangeAdress1} value={address1} />
            </Col>
            <Col md={12} style={styles.formFieldsSpace}>
              <Label>Address2</Label>
              <Input type="text" onChange={onChangeAdress2} value={address2} />
            </Col>
            <Col xs={6} style={styles.formFieldsSpace}>
              <FormGroup style={styles.formFieldsSpace}>
                <Label for="exampleCheckbox">
                  <u>Shipping Method</u>
                </Label>
                <FormGroup check>
                  <Label check>
                    <Input
                      type="radio"
                      onChange={() => onChangeShipppingMethod(1)}
                      checked={shippingMethod === 1}
                    />{" "}
                    express (2 days shipping)
                  </Label>
                </FormGroup>
                <FormGroup check disabled>
                  <Label check>
                    <Input
                      type="radio"
                      onChange={() => onChangeShipppingMethod(2)}
                      checked={shippingMethod === 2}
                    />{" "}
                    1 week shipping
                  </Label>
                </FormGroup>
              </FormGroup>
            </Col>
            <div className="d-flex align-items-center">
              <Button
                disabled={false}
                onClick={() => {
                  if (
                    [
                      ...new Set(
                        Object.entries(validatorClient).map(([k, v]) => v)
                      )
                    ].sort()[0]
                  ) {
                    return (
                      stepsUnlock("step3"),
                      addUserAddress({
                        firstName,
                        lastName,
                        country,
                        city,
                        province,
                        postalCode,
                        phoneNumber,
                        address1,
                        address2
                      })
                    );
                  } else {
                    formValidator(true);
                  }
                }}
              >
                continue
              </Button>
            </div>
          </Row>
          {warningValidator(formIsValid)}
        </Collapse>
      </ListGroupItem>
    </div>;
  }
}
