import React, { Component } from "react";
import { Link } from "react-router-dom";
import {
  Row,
  Col,
  Button,
  Card,
  CardImg,
  CardBody,
  Alert,
  Container
} from "react-bootstrap";

const styles = {
  cardTitle: {
    display: "flex",
    justifyContent: "center",
    fontSize: "20px"
  },
  cardBtn: {
    textAlign: "center"
  },
  card: {
    marginBottom: "20px"
  }
};

const categoriesMenData = [
  {
    imgSrc:
      "https://images.unsplash.com/photo-1490367532201-b9bc1dc483f6?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=717ae13fa0e515d1c7c9e92456439845&dpr=1&auto=format&fit=crop&w=1000&q=80&cs=tinysrgb",
    cardTitle: "Polos",
    linkCategory: "Polos"
  },
  {
    imgSrc:
      "https://images.unsplash.com/photo-1530856021941-02c71be5926f?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=8d29673aa02db77423cf5ca770bd6d8e&dpr=1&auto=format&fit=crop&w=1000&q=80&cs=tinysrgb",
    cardTitle: "Shirts",
    linkCategory: "Shirts"
  },
  {
    imgSrc:
      "https://images.unsplash.com/photo-1421986386270-978ed214ec60?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=0f6cec57034960f50caf64c5af9c26d4&dpr=1&auto=format&fit=crop&w=1000&q=80&cs=tinysrgb",
    cardTitle: "Pants",
    linkCategory: "Pants"
  },
  {
    imgSrc:
      "https://images.unsplash.com/photo-1530856021941-02c71be5926f?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=8d29673aa02db77423cf5ca770bd6d8e&dpr=1&auto=format&fit=crop&w=1000&q=80&cs=tinysrgb",
    cardTitle: "Jackets",
    linkCategory: "Jackets"
  }
];

export default class Men extends Component {
  render() {
    const decorationData = categoriesMenData;
    return (
      <div>
        decorationData.map(menx=>(
        <Col md="4" style={styles.card} key={x.cardTitle}>
          <Card>
            <div style={styles.cardBtn} onClick={() => x.cardTitle}>
              <Link to={`/productslist/${gender}/`}>
                <CardImg
                  src={x.imgSrc}
                  alt="Card image cap"
                  style={{ boxShadow: "0px 0px 7px 0px rgba(0,0,0,0.75)" }}
                />
                <div
                  style={{
                    position: "absolute",
                    top: "40%",
                    textAlign: "center",
                    width: "100%",
                    color: "white",
                    fontSize: "200%"
                  }}
                >
                  <b> {x.cardTitle} </b>
                </div>
              </Link>
            </div>
          </Card>
        </Col>
        ))
      </div>
    );
  }
}
