import React, { Component } from "react";
import { ListGroup, ListGroupItem, Col, Row } from "react-bootstrap";

export default class Summary extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cartItems: [],
      totalPrice: ""
    };
  }
  componentDidMount() {
    let cartArray = [];
    cartArray = JSON.parse(localStorage.getItem("cartItem")) || [];
    this.setState({ cartItems: cartArray }, () => {
      if (this.state.cartItems.length < 1) {
        this.setState({ emptyMessage: "Empty Cart", totalPrice: 0 });
      }
      if (this.state.cartItems.length !== 0) {
        let price = this.state.cartItems[0].price;
        if (this.state.cartItems.length !== 0) {
          for (let i = 1; i < this.state.cartItems.length; i++) {
            price = price + this.state.cartItems[i].price;
          }
          this.setState({ totalPrice: price });
        } else this.setState({ totalPrice: price });
      }
    });
  }
  render() {
    let shipping;
    this.state.totalPrice === 0 ? (shipping = 0) : (shipping = 100);
    return (
      <div>
        <ListGroup>
          <ListGroupItem>Order Summary</ListGroupItem>
          <ListGroupItem>
            {this.state.cartItems.map((item, id) => (
              <Row key={item.id}>
                <Col xs="8">
                  <p>
                    x{item.quantity} {item.title}
                  </p>
                </Col>
                <Col xs="4">
                  <p>{item.price}$</p>
                </Col>
              </Row>
            ))}
          </ListGroupItem>
          <ListGroupItem>
            <Row>
              <Col xs="8">
                <p>Subtotal</p>
              </Col>
              <Col xs="4">
                <p>Rs: {this.state.totalPrice}</p>
              </Col>
            </Row>
            <Row>
              <Col xs="8">
                <p>Shipping</p>
              </Col>
              <Col xs="4">
                <p>Rs: {shipping}</p>
              </Col>
            </Row>
            <Row>
              <Col xs="8">
                <p>Tax</p>
              </Col>
              <Col xs="4">
                <p>Rs: 0</p>
              </Col>
            </Row>
          </ListGroupItem>
          <ListGroupItem>
            <Row>
              <Col xs="8">
                <p>Total</p>
              </Col>
              <Col xs="4">
                <b style={{ fontSize: "25px" }}>
                  Rs: {this.state.totalPrice + shipping}
                </b>
              </Col>
            </Row>
          </ListGroupItem>
        </ListGroup>
      </div>
    );
  }
}
