import React, { Component } from "react";
import axios from "axios";
import ItemTable from "./ItemTable";
import AddProduct from "./AddProduct";
impo
import { Tab, Nav, Row, Col } from "react-bootstrap";

export default class AdminControl extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      activeTab: "1"
    };
  }

  componentDidMount = async () => {
    axios
      .get("http://localhost:4000/products")
      .then(res => {
        const products = res.data;
        this.setState({ products });
      })
      .catch(err => {
        console.log(err);
      });
  };

  toggle = tab =>
    this.state.activeTab !== tab && this.setState({ activeTab: tab });

  render() {
    return (
      <div>
        <Header />
        <Tab.Container defaultActiveKey="first">
          <Row>
            <Col sm={3}>
              <Nav variant="pills" className="flex-column">
                <Nav.Item>
                  <Nav.Link
                    eventKey="first"
                    style={{
                      cursor: "pointer",
                      backgroundColor: "#66bceb",
                      color: "white"
                    }}
                  >
                    Add Items
                  </Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link
                    eventKey="second"
                    style={{
                      cursor: "pointer",
                      backgroundColor: "#cd5957",
                      color: "white"
                    }}
                  >
                    Update/Delete Items
                  </Nav.Link>
                </Nav.Item>
              </Nav>
            </Col>
            <Col sm={9}>
              <Tab.Content>
                <Tab.Pane eventKey="first">
                  {" "}
                  <AddProduct
                    stylesTab2={{
                      cursor: "pointer",
                      backgroundColor: "#cd5957",
                      color: "white"
                    }}
                  />
                </Tab.Pane>
                <Tab.Pane eventKey="second">
                  <ItemTable
                    stylesTab1={{
                      cursor: "pointer",
                      backgroundColor: "#66bceb",
                      color: "white"
                    }}
                  />
                </Tab.Pane>
              </Tab.Content>
            </Col>
          </Row>
        </Tab.Container>
      </div>
    );
  }
}
