import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class SizeSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      sizes: []
    };
  }

  render() {
    if (this.props.size) {
      this.state.sizes = this.props.size;
      console.log("sizes are", this.state.sizes);
    }

    const dropdown = this.state.sizes.map(size => (
      <Dropdown.Item
        key={size}
        onClick={() => {
          this.props.handleSizeSelection(size);
        }}
      >
        {size}
      </Dropdown.Item>
    ));
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="secondary">
            Size: "Click to choose"
          </Dropdown.Toggle>
          <Dropdown.Menu>{dropdown}</Dropdown.Menu>
        </Dropdown>
      </div>
    );
  }
}
