import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class SizeSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount() {
    // this.setState({ size: this.props.size });
  }
  render() {
    const sizes = Jsthis.props.size;
    console.log("size is", sizes);
    console.log(typeof sizes);
    console.log("Checking array", Array.isArray(sizes));
    console.log("stringify", JSON.stringify(sizes));
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="info" id="dropdown-basic">
            Select Size
          </Dropdown.Toggle>
          <h1>{[sizes]}</h1>
          {sizes.map((size, id) => (
            <Dropdown.Menu key={id}>
              <Dropdown.Item>{size}</Dropdown.Item>
              <Dropdown.Item>Another action</Dropdown.Item>
              <Dropdown.Item>Something else</Dropdown.Item>
            </Dropdown.Menu>
          ))}
        </Dropdown>
      </div>
    );
  }
}
