import React, { Component } from "react";
import axios from "axios";
import { Card, Button, Badge } from "react-bootstrap";
import { Link } from "react-router-dom";

class ProductDiscription extends Component {
  _isMounted = false;
  constructor(props) {
    super(props);
    this.state = {
      products: {},
      count: Number
    };
  }
  componentDidMount() {
    this._isMounted = true;
    const id = this.props.match.params.id;
    console.log("recived id is", id);
    axios.get(`http://localhost:4000/movies/${id}`).then(data => {
      this.setState({ movies: data.data });
    });
  }
  componentDidUpdate() {
    this._isMounted = true;
    const id = this.props.match.params.id;
    axios.get(`http://localhost:4000/movies/${id}`).then(data => {
      this.setState({ movies: data.data });
    });
  }
  increment = () => {
    // if (this.state.movies.like === undefined) {
    //   this.setState({ like: 0 });
    // }
    const like = parseInt(this.state.movies.like + 1);
    const id = this.state.movies._id;
    console.log("id in increment is", id);
    axios
      .put(`http://localhost:4000/movies/${id}/1`, {
        like: like
      })
      .then(res => {
        console.log(res);
      })
      .catch(err => {
        console.log(err);
      });
  };
  deleteCard = e => {
    this._isMounted = true;
    e.preventDefault();
    const delId = this.state.movies._id;
    axios.delete(`http://localhost:4000/movies/${delId}`).then(res => {
      alert(res.data.message);
      console.log(res);
      console.log(res.data);
    });
  };
  componentWillUnmount() {
    this._isMounted = false;
  }
  render() {
    return (
      <div className="container">
        <Card style={{ width: "60rem" }}>
          <Card.Img variant="top" src={this.state.movies.imageURL} />
          <Card.Body>
            <Card.Title>{this.state.movies.title}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">
              {this.state.movies.subTitle}
            </Card.Subtitle>
            <Card.Text>{this.state.movies.description}</Card.Text>
            <Button onClick={this.increment}>
              Like <Badge variant="light">{this.state.movies.like || 0}</Badge>
            </Button>
          </Card.Body>
          <Card.Footer>
            <Link to={`/edit/${this.state.movies._id}`}>
              <Button variant="primary">Edit Card</Button>
            </Link>{" "}
            <Button variant="danger" onClick={this.deleteCard}>
              Delete Card
            </Button>
          </Card.Footer>
        </Card>
      </div>
    );
  }
}
export default ProductDiscription;
