import React, { Component } from "react";
import axios from "axios";
import ItemTable from "./ItemTable";
import AddProduct from "./AddProduct";
import { TabContent, TabPane, Nav, NavItem, NavLink } from "react-bootstrap";

export default class AdminControl extends Component {
  constructor(props) {
    super(props);
    this.state = {
      apiList: [],
      activeTab: "1"
    };
  }

  componentDidMount = async () => {
    try {
      const response = await axios.get("/api/productsdata");
      const apiList = await response.data;
      this.setState({ apiList });
    } catch (error) {
      console.log(error);
    }
  };

  toggle = tab =>
    this.state.activeTab !== tab && this.setState({ activeTab: tab });

  render() {
    return (
      <div>
        <Nav tabs>
          <NavItem>
            <NavLink
              style={{
                cursor: "pointer",
                backgroundColor: "#66bceb",
                color: "white"
              }}
              onClick={() => {
                this.toggle("1");
              }}
            >
              <b>Add Items</b>
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              style={{
                cursor: "pointer",
                backgroundColor: "#cd5957",
                color: "white"
              }}
              onClick={() => {
                this.toggle("2");
              }}
            >
              <b>Update/delete items</b>
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={this.state.activeTab}>
          <TabPane tabId="1">
            <ItemTable
              stylesTab1={{
                cursor: "pointer",
                backgroundColor: "#66bceb",
                color: "white"
              }}
            />
          </TabPane>
          <TabPane tabId="2">
            <AdminTablesItems
              stylesTab2={{
                cursor: "pointer",
                backgroundColor: "#cd5957",
                color: "white"
              }}
            />
          </TabPane>
        </TabContent>
      </div>
    );
  }
}
