import React, { Component } from "react";
import { Dropdown } from "react-bootstrap";

export default class ColorSelection extends Component {
  constructor(props) {
    super(props);
    this.state = {
      colors: []
    };
  }
  componentDidUpdate() {
    console.log("Color is", this.props.color);
    if (this.state.color.length === 0) {
      this.setState({ colors: this.props.color });
    }
  }
  render() {
    if (this.props.color) {
      this.state.colors = this.props.color;
    }
    const dropdown = this.state.colors.map(color => (
      <Dropdown.Item key={color}>{color}</Dropdown.Item>
    ));
    return (
      <div>
        <Dropdown>
          <Dropdown.Toggle variant="info" id="dropdown-basic">
            Select Color
          </Dropdown.Toggle>
          <Dropdown.Menu>{dropdown}</Dropdown.Menu>
        </Dropdown>
      </div>
    );
  }
}
