import React, { Component } from "react";
import { Form, FormGroup, Label, Input, Row, Col, Button } from "reactstrap";
import Layout from "../Layout";
import axios from "axios";
import { Link } from "react-router-dom";

export default class Signup extends Component {
  constructor(props) {
    super(props);
    this.state = {
      firstname: "",
      lastname: "",
      email: "",
      password: "",
      error: [],
      success: "",
      errorMessage: "",
    };
  }
  changeHandler = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  submitHandler = (e) => {
    e.preventDefault();
    const { firstname, lastname, email, password } = this.state;
    axios
      .put("http://localhost:4000/signup", {
        firstname,
        lastname,
        email,
        password,
      })
      .then((res) => {
        console.log("response is", res);
        this.setState({ success: res.data.message });
      })
      .catch((err) => {
        // console.log("error is", err.response.data.data);
        this.setState({ error: err.response.data.data }, () => {
          console.log("error is", this.state.error);
        });
      });
    const result = this.state.error.find((err) => err.param === "email");
    console.log("result is", result);
    this.setState({errorMessage:re});
  };
  render() {
    const { firstname, lastname, email, password } = this.state;
    return (
      <Layout>
        <Form
          className="container"
          style={{ paddingTop: "80px", paddingBottom: "100px" }}
          onSubmit={this.submitHandler}
        >
          <Row form>
            <Col>
              <FormGroup>
                <Label for="exampleEmail">First Name</Label>
                <Input
                  type="text"
                  name="firstname"
                  value={firstname}
                  onChange={this.changeHandler}
                  placeholder="First Name"
                />
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Label for="examplePassword">Last Name</Label>
                <Input
                  type="text"
                  name="lastname"
                  vale={lastname}
                  onChange={this.changeHandler}
                  placeholder="Last Name"
                />
              </FormGroup>
            </Col>
          </Row>

          <Row form>
            <Col>
              <FormGroup>
                <Label for="exampleEmail">Email</Label>
                <Input
                  type="email"
                  name="email"
                  value={email}
                  id="exampleEmail"
                  onChange={this.changeHandler}
                  placeholder="Email"
                />
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Label for="examplePassword">Password</Label>
                <Input
                  type="password"
                  name="password"
                  value={password}
                  id="examplePassword"
                  onChange={this.changeHandler}
                  placeholder="Password"
                />
              </FormGroup>
            </Col>
          </Row>
          <Row>
            <Button type="submit">Sign up</Button> &nbsp;
            <h6 style={{ marginTop: "10px" }}>
              {" "}
              Already have an account? <Link to="/login">login</Link>
            </h6>
          </Row>
        </Form>
      </Layout>
    );
  }
}
