import React, { Component } from "react";
import axios from "axios";
import { Card, Badge, CardColumns, Col, Row, CardImg } from "react-bootstrap";
import Header from "../homepage/Header";
import { Link } from "react-router-dom";

class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
      error: ""
    };
  }
  componentDidMount() {
    const tag = this.props.match.params.tag;
    console.log("Category is params is ", tag);
    axios
      .get(`http://localhost:4000/category/${tag}`)
      .then(data => {
        if (data.data.message) {
          this.setState({ error: data.data.message });
          console.log("error is", this.state.error);
        } else {
          this.setState({ products: data.data });
          console.log("data in  category", this.state.products);
        }
      })
      .catch(err => {
        console.log("error is", err);
      });
  }

  render() {
    return (
      // <div>
      //   <Header />
      //   <div className="mt-5">
      //     {" "}
      //     {this.state.error && <h1>{this.state.error}</h1>}
      //   </div>
      //   {this.state.products && (
      //     <div className="mt-5">
      //       <CardColumns>
      //         {this.state.products.map((product, id) => (
      //           <Card key={id}>
      //             <Link to={`/productdiscription/${product._id}`}>
      //               <Card.Img variant="top" src={product.images} alt="image" />
      //               <Card.Body>
      //                 <Card.Title>{product.title}</Card.Title>
      //                 <Card.Subtitle>Rs:{product.price}</Card.Subtitle>
      //                 <Card.Text>{product.description}</Card.Text>
      //               </Card.Body>
      //               <Card.Body>
      //                 <Badge variant="primary">{product.rating}</Badge>
      //               </Card.Body>
      //             </Link>
      //           </Card>
      //         ))}
      //       </CardColumns>
      //     </div>
      //   )}
      // </div>
      <div>
        <Header />
        <div className="mt-5">
          {" "}
          {this.state.error && <h1>{this.state.error}</h1>}
        </div>
        {this.state.products && (
        <Col md="12">
          <h1
            style={{ fontSize: "40px", textAlign: "center", padding: "20px" }}
          >
            {this.props.match.params.tag}
          </h1>
          <Row>
            {this.state.product.map((item, id) => (
              <Col md="4" style={{ marginBottom: "20px" }} key={id}>
                <h1>{item.cardTitle}</h1>
                <Link to={`/productlist/${item.cardTitle}`}>
                  <Card>
                    <div
                      style={{ textAlign: "center" }}
                      onClick={() => item.cardTitle}
                    >
                      <CardImg
                        src={item.imgSrc}
                        alt="Card image cap"
                        style={{
                          boxShadow: "0px 0px 7px 0px rgba(0,0,0,0.75)"
                        }}
                      />
                      <div
                        style={{
                          position: "absolute",
                          top: "40%",
                          textAlign: "center",
                          width: "100%",
                          color: "white",
                          fontSize: "200%"
                        }}
                      >
                        <b> {item.cardTitle} </b>
                      </div>
                    </div>
                  </Card>
                </Link>
              </Col>
            ))}
          </Row>
        </Col> )}
      </div>
    );
  }
}
export default ProductList;
