import React, { Component } from "react";
import { Button, Form, Container } from "react-bootstrap";
import axios from "axios";

class Add extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "tteeest",
      color: "blue, red",
      size: "XS, L",
      tags: "Polos",
      images:
        "https://i.ytimg.com/vi/Bor5lkRyeGo/hqdefault.jpg, https://i.ytimg.com/vi/Bor5lkRyeGo/hqdefault.jpg",
      description: "",
      price: 0
    };
  }

  onSubmit = (title, price, color, size, tags, images, description) => {
    axios
      .post("http://localhost:4000/products/add", {
        title,
        price,
        color: (color.slice(0) + "").replace(/\s/g, "").split(","),
        size: (size.slice(0) + "").replace(/\s/g, "").split(","),
        tags: (tags.slice(0) + "").replace(/\s/g, "").split(","),
        images: (images.slice(0) + "").replace(/\s/g, "").split(","),
        description
      })
      .then(() => {
        window.location.reload(true);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  onChangeTitle = e => this.setState({ title: e.target.value });
  onChangePrice = e => this.setState({ price: e.target.value });
  onChangeColor = e => this.setState({ color: [e.target.value] });
  onChangesize = e => this.setState({ size: e.target.value });
  onChangeTags = e => this.setState({ tags: [e.target.value] });
  onChangeImages = e => this.setState({ images: [e.target.value] });
  onChangeDescription = e => this.setState({ description: e.target.value });

  render() {
    const { title, price, color, size, tags, images, description } = this.state;
    return (
      <Container style={{ paddingTop: "50px", paddingBottom: "50px" }}>
        <h1>Add new item</h1>
        <Form>
          <FormGroup>
            <Form.Label for="exampleEmail">Item's name</Form.Label>
            <Input
              placeholder="example: cool polo"
              value={this.state.title}
              onChange={this.onChangeTitle}
            />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">Price</Label>
            <Input
              placeholder="example: 43"
              value={this.state.price}
              onChange={this.onChangePrice}
            />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">color available</Label>
            <Input
              placeholder="example: color1, color2, color3"
              value={this.state.color}
              onChange={this.onChangeColor}
            />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">size available</Label>
            <Input
              placeholder="example: XS, L, XL"
              value={this.state.size}
              onChange={this.onChangesize}
            />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">Category</Label>
            <Input
              placeholder="example: Polo"
              value={this.state.tags}
              onChange={this.onChangeTags}
            />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">Images</Label>
            <Input
              placeholder="example: http://link1.jpg, http://link2.jpg"
              value={this.state.images}
              onChange={this.onChangeImages}
            />
          </FormGroup>
          <FormGroup>
            <Label for="examplePassword">Description</Label>
            <Input
              type="textarea"
              value={this.state.description}
              onChange={this.onChangeDescription}
            />
          </FormGroup>
        </Form>
        <Button
          onClick={() =>
            this.onSubmit(title, price, color, size, tags, images, description)
          }
        >
          Submit
        </Button>
      </Container>
    );
  }
}

export default Add;
